import React, { useState, useRef, useEffect, useCallback } from 'react';
import * as XLSX from 'xlsx';
import './Momi.css';

const Momi = () => {
  const [formattedData, setFormattedData] = useState([]);
  const [startNumber, setStartNumber] = useState('');
  const [offset, setOffset] = useState('');
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const [dragActive, setDragActive] = useState(false);
  const [progress, setProgress] = useState(0);
  const fileInputRef = useRef(null);
  const outputRef = useRef(null);
  const [previewData, setPreviewData] = useState(null);
  const [settings, setSettings] = useState({
    autoCopy: true,
    darkMode: false,
    showPreview: true
  });
  const [showTutorial, setShowTutorial] = useState(false);

  useEffect(() => {
    let interval;
    if (loading) {
      interval = setInterval(() => {
        setProgress(prev => Math.min(prev + 10, 90));
      }, 200);
    }
    return () => {
      clearInterval(interval);
      if (!loading && progress < 100) {
        setProgress(100);
        setTimeout(() => setProgress(0), 500);
      }
    };
  }, [loading]);

  useEffect(() => {
    if (errors.file || errors.startNumber || errors.offset) {
      const timer = setTimeout(() => setErrors({}), 5000);
      return () => clearTimeout(timer);
    }
  }, [errors]);

  const validateInputs = () => {
    const newErrors = {};
    if (!fileInputRef.current.files.length) {
      newErrors.file = 'Ë´ãÈÅ∏Êìá‰∏ÄÂÄã Excel Ê™îÊ°à„ÄÇ';
    }
    if (!Number.isInteger(Number(startNumber)) || startNumber < 1) {
      newErrors.startNumber = 'Ë´ãËº∏ÂÖ•ÊúâÊïàÁöÑÊ≠£Êï¥Êï∏„ÄÇ';
    }
    if (!Number.isInteger(Number(offset)) || offset < 0) {
      newErrors.offset = 'Ë´ãËº∏ÂÖ•ÊúâÊïàÁöÑÈùûË≤†Êï¥Êï∏„ÄÇ';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const file = e.dataTransfer.files[0];
      const isValidFileType = (fileName) => 
        ['.xlsx', '.xls', 'application/vnd.ms-excel', 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet']
          .some(ext => fileName.endsWith(ext) || file.type.includes(ext));

      if (isValidFileType(file.name)) {
        fileInputRef.current.files = e.dataTransfer.files;
        setErrors(prev => ({ ...prev, file: '' }));
      } else {
        setErrors(prev => ({ ...prev, file: 'Ë´ã‰∏äÂÇ≥ Excel Ê™îÊ°à (.xlsx Êàñ .xls)' }));
      }
    }
  };

  const memoizedProcessor = useCallback((jsonData, startRowIndex) => {
    const processRow = (row, index) => {
      const currentNumber = parseInt(startNumber) + index;
      return `üêçüêçÊñ∞Âπ¥Âø´Ê®Çü•∞üéÜüéÜüéÜüéÜ\n\nÁ¨¨${currentNumber}‰ΩçÊäïÁ®ø‰∫∫ÂöüÂï¶ÔΩû\nÂêçÂ≠óÔºö${row[1] || 'N/A'}\nÂπ¥ÈΩ°Ôºö${row[2] || 'N/A'}\nË∫´È´òÔºö${row[3] || 'N/A'}\n\nÊèèËø∞Ëá™Â∑≤Ôºö${row[4] || 'N/A'}\n\nË¶ÅÊ±ÇÔºö${row[5] || 'N/A'}\n\nËÅØÁµ°ÊñπÂºèÔºö${row[6] || 'N/A'}\n\nÂ¶ÇÊûúÊúâÁ∑£‰∫∫ÊÉ≥Ë™çË≠òÁÑ°ÁïôtgÊó¢ÊäïÁ®ø‰∫∫ÔºåÂèØ‰ª•dmÂπ≥Âè∞ÁöÑÔºÅüôäüôäüôäüôäüôä\nÊäïÁ®ølink‰øÇ‰∏ªÈ†Åüß®Â§ßÂÆ∂Èö®ÊÑèÊäïÁ®øüéê\n\n`;
    };

    return jsonData.slice(startRowIndex).map((row, index) => processRow(row, index));
  }, [startNumber, offset]);

  const handleProcess = async () => {
    if (!validateInputs()) return;

    setLoading(true);
    setSuccessMessage('');
    setProgress(0);

    try {
      const file = fileInputRef.current.files[0];
      const data = await readFile(file);
      const workbook = XLSX.read(data, { type: 'array' });
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];
      const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

      const startRowIndex = parseInt(startNumber) + parseInt(offset) - 1;
      if (startRowIndex >= jsonData.length) {
        setErrors({ file: `Ëµ∑ÂßãÊï∏Â≠óÂä†ÂÅèÁßªÂÄºÊåáÂêëÁöÑË°åÔºàÁ¨¨ ${startRowIndex + 1} Ë°åÔºâË∂ÖÂá∫ÁØÑÂúç„ÄÇ` });
        return;
      }

      const newFormattedData = memoizedProcessor(jsonData, startRowIndex);

      setFormattedData(newFormattedData);
      setSuccessMessage('ËôïÁêÜÂÆåÊàêÔºÅÊÇ®ÁèæÂú®ÂèØ‰ª•‰∏ãËºâÊ™îÊ°à„ÄÇ');

      // Scroll to output
      if (outputRef.current) {
        outputRef.current.scrollIntoView({ behavior: 'smooth' });
      }
    } catch (error) {
      setErrors({ file: 'ËôïÁêÜÈÅéÁ®ã‰∏≠Âá∫ÁèæÈåØË™§ÔºåË´ãÊ™¢Êü•ÊÇ®ÁöÑ Excel Ê™îÊ°àÊ†ºÂºè„ÄÇ' });
    } finally {
      setLoading(false);
    }
  };

  const readFile = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (e) => resolve(e.target.result);
      reader.onerror = (error) => reject(error);
      reader.readAsArrayBuffer(file);
    });
  };

  const handleDownload = () => {
    const text = formattedData.join('\n\n------------------------\n\n');
    const blob = new Blob([text], { type: 'text/plain;charset=utf-8' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'formatted_data.txt';
    link.click();
    URL.revokeObjectURL(link.href);

    // Show success animation
    setSuccessMessage('Ê™îÊ°àÂ∑≤ÊàêÂäü‰∏ãËºâÔºÅ');
    setTimeout(() => setSuccessMessage(''), 3000);
  };

  const handleCopyToClipboard = async () => {
    const text = formattedData.join('\n\n------------------------\n\n');
    try {
      await navigator.clipboard.writeText(text);
      setSuccessMessage('Â∑≤Ë§áË£ΩÂà∞Ââ™Ë≤ºÊùøÔºÅ');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setErrors({ clipboard: 'Ë§áË£ΩÂ§±ÊïóÔºåË´ãÊâãÂãïË§áË£Ω„ÄÇ' });
    }
  };

  const handlePreview = async () => {
    if (!fileInputRef.current.files.length) return;

    const file = fileInputRef.current.files[0];
    const data = await readFile(file);
    const workbook = XLSX.read(data, { type: 'array' });
    const sheetName = workbook.SheetNames[0];
    const worksheet = workbook.Sheets[sheetName];
    const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

    setPreviewData(jsonData.slice(0, 5)); // Show first 5 rows
  };

  const toggleSetting = (setting) => {
    setSettings(prev => ({
      ...prev,
      [setting]: !prev[setting]
    }));
  };

  return (
    <div className="container">
      <h1>Momi Ê†ºÂºèËΩâÊèõÂô®</h1>

      <div className="settings-panel">
        <button 
          className="settings-toggle"
          onClick={() => toggleSetting('showSettings')}
        >
          <i className="fas fa-cog"></i>
        </button>

        {settings.showSettings && (
          <div className="settings-content">
            <label>
              <input
                type="checkbox"
                checked={settings.autoCopy}
                onChange={() => toggleSetting('autoCopy')}
              />
              Ëá™ÂãïË§áË£ΩÁµêÊûú
            </label>
            <label>
              <input
                type="checkbox"
                checked={settings.darkMode}
                onChange={() => toggleSetting('darkMode')}
              />
              Ê∑±Ëâ≤Ê®°Âºè
            </label>
          </div>
        )}
      </div>

      <div 
        className={`drop-zone ${dragActive ? 'active' : ''}`}
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
      >
        <div className="drop-content">
          <i className="fas fa-file-upload"></i>
          <p>ÊãñÊîæ Excel Ê™îÊ°àËá≥Ê≠§ÊàñÈªûÊìäÈÅ∏Êìá</p>
          <p className="file-types">ÊîØÊè¥Ê†ºÂºè: .xlsx, .xls</p>
        </div>
        <input 
          id="file-input"
          type="file" 
          ref={fileInputRef}
          accept=".xlsx, .xls"
          onChange={() => setErrors(prev => ({ ...prev, file: '' }))}
        />
        {errors.file && (
          <div className="error-message">
            <i className="fas fa-exclamation-circle"></i>
            {errors.file}
          </div>
        )}
      </div>

      <div className="input-group">
        <label>
          <i className="fas fa-sort-numeric-down button-icon"></i>
          Ëµ∑ÂßãÊï∏Â≠ó
        </label>
        <input
          type="number"
          value={startNumber}
          onChange={(e) => {
            setStartNumber(e.target.value);
            setErrors(prev => ({ ...prev, startNumber: '' }));
          }}
          placeholder="Ë´ãËº∏ÂÖ•Ëµ∑ÂßãÊï∏Â≠ó"
        />
        {errors.startNumber && (
          <div className="error-message">
            <i className="fas fa-exclamation-circle"></i>
            {errors.startNumber}
          </div>
        )}
      </div>

      <div className="input-group">
        <label>
          <i className="fas fa-arrows-alt-v button-icon"></i>
          ÂÅèÁßªÂÄº
        </label>
        <input
          type="number"
          value={offset}
          onChange={(e) => {
            setOffset(e.target.value);
            setErrors(prev => ({ ...prev, offset: '' }));
          }}
          placeholder="Ë´ãËº∏ÂÖ•ÂÅèÁßªÂÄº"
        />
        {errors.offset && (
          <div className="error-message">
            <i className="fas fa-exclamation-circle"></i>
            {errors.offset}
          </div>
        )}
      </div>

      <button 
        className="process-btn" 
        onClick={handleProcess} 
        disabled={loading}
      >
        <i className={`fas ${loading ? 'fa-spinner fa-spin' : 'fa-file-import'} button-icon`}></i>
        {loading ? 'ËôïÁêÜ‰∏≠...' : 'ËôïÁêÜÊ™îÊ°à'}
      </button>

      {loading && (
        <div className="progress-bar">
          <div 
            className="progress-bar-fill" 
            style={{ width: `${progress}%` }}
          ></div>
        </div>
      )}

      {successMessage && (
        <div className="success-message">
          <i className="fas fa-check-circle"></i>
          {successMessage}
        </div>
      )}

      {formattedData.length > 0 && (
        <div ref={outputRef}>
          <div className="output-header">
            <h3>
              <i className="fas fa-file-alt button-icon"></i>
              ËôïÁêÜÁµêÊûú
            </h3>
            <button 
              className="copy-btn"
              onClick={handleCopyToClipboard}
              title="Ë§áË£ΩÂà∞Ââ™Ë≤ºÊùø"
            >
              <i className="fas fa-copy"></i>
            </button>
          </div>
          <div className="output">
            {formattedData.join('\n\n------------------------\n\n')}
          </div>
          <button className="download-btn" onClick={handleDownload}>
            <i className="fas fa-download button-icon"></i>
            ‰∏ãËºâÊ™îÊ°à
          </button>
        </div>
      )}

      {previewData && (
        <div className="preview-section">
          <h3>
            <i className="fas fa-eye button-icon"></i>
            Ê™îÊ°àÈ†êË¶Ω (Ââç5Ë°å)
          </h3>
          <div className="preview-table">
            {previewData.map((row, index) => (
              <div key={index} className="preview-row">
                {row.map((cell, cellIndex) => (
                  <span key={cellIndex} className="preview-cell">
                    {cell || 'N/A'}
                  </span>
                ))}
              </div>
            ))}
          </div>
        </div>
      )}

      {showTutorial && (
        <div className="tutorial-overlay">
          <div className="tutorial-content">
            <h2>‰ΩøÁî®ÊïôÂ≠∏ <i className="fas fa-graduation-cap"></i></h2>
            <ol>
              <li>ÈÅ∏Êìá Excel Ê™îÊ°àÔºàÊîØÊè¥ .xlsx Âíå .xls Ê†ºÂºèÔºâ</li>
              <li>Ëº∏ÂÖ•Ëµ∑ÂßãÁ∑®ËôüÂíåÂÅèÁßªÂÄº</li>
              <li>ÈªûÊìä„ÄåËôïÁêÜÊ™îÊ°à„ÄçÊåâÈàï</li>
              <li>È†êË¶Ω‰∏¶‰∏ãËºâÁµêÊûú</li>
            </ol>
            <button 
              className="close-tutorial"
              onClick={() => setShowTutorial(false)}
            >
              ÈóúÈñâÊïôÂ≠∏
            </button>
          </div>
        </div>
      )}

      <button 
        className="help-button"
        onClick={() => setShowTutorial(true)}
      >
        <i className="fas fa-question-circle"></i>
      </button>

      {loading && <div role="alert" aria-busy="true" className="sr-only">ËôïÁêÜÈÄ≤Ë°å‰∏≠</div>}
    </div>
  );
};

export default Momi;
